script:
frappe.ui.form.on('Top-up Transactions', {
    operation_type: function(frm) {
        // Check if there are any rows in the transactions_account table
        if (frm.doc.transactions_account && frm.doc.transactions_account.length > 0) {
            // Set the operation_type field for the first row in the transactions_account table
            frm.doc.transactions_account[0].operation_type = frm.doc.operation_type;

            // Refresh the field in the table to reflect the changes
            frm.refresh_field('transactions_account');
        }
    },
    account: function(frm) {
        // Check if there are any rows in the transactions_account table
        if (frm.doc.transactions_account && frm.doc.transactions_account.length > 0) {
            // Set the account field for the first row in the transactions_account table
            frm.doc.transactions_account[0].account = frm.doc.account;

            // Refresh the field in the table to reflect the changes
            frm.refresh_field('transactions_account');
        }
    }
});
